package week1.day3;

public class OddNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int input=50;
		for(int i=0;i<=input;i++) {
			int c=i%2;
		
		if(c==0) {
			System.out.println("Even Numbers :"+i);
			
		}
		else {
			System.out.println("odd Numbers :"+i);
		
	   }
	}

}
}
